(12, 1, '2024-12-10 11:39:34', '2024-12-10 10:39:34', '<!-- wp:html -->\n<!DOCTYPE html>\n<html lang=\"es\">\n<head>\n    <meta charset=\"UTF-8\">\n    <meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\">\n    <title>Máquina Tragamonedas Profesional</title>\n    <style>\n        @import url(\'https://fonts.googleapis.com/css2?family=Roboto:wght@400;700&display=swap\');\n\n        body {\n            font-family: \'Roboto\', sans-serif;\n            background: linear-gradient(135deg, #2c3e50, #4ca1af);\n            color: #ffffff;\n            text-align: center;\n            display: flex;\n            justify-content: center;\n            align-items: center;\n            height: 100vh;\n            margin: 0;\n        }\n        .slot-machine {\n            background-color: #1e272e;\n            padding: 30px;\n            border-radius: 15px;\n            box-shadow: 0 10px 30px rgba(0, 0, 0, 0.5);\n            width: 320px;\n        }\n        .reels {\n            display: flex;\n            justify-content: center;\n            margin-bottom: 30px;\n        }\n        .reel {\n            width: 80px;\n            height: 80px;\n            background-color: #333;\n            margin: 0 5px;\n            display: flex;\n            align-items: center;\n            justify-content: center;\n            font-size: 24px;\n            border-radius: 10px;\n            color: #ffcc00;\n            font-weight: bold;\n            box-shadow: inset 0 0 10px rgba(0, 0, 0, 0.5);\n        }\n        .controls {\n            margin-bottom: 30px;\n        }\n        .controls button {\n            padding: 12px 24px;\n            font-size: 16px;\n            margin: 0 10px;\n            cursor: pointer;\n            border: none;\n            border-radius: 5px;\n            background-color: #e74c3c;\n            color: #ffffff;\n            box-shadow: 0 5px 15px rgba(0, 0, 0, 0.3);\n            transition: background-color 0.3s, transform 0.3s;\n        }\n        .controls button:hover {\n            background-color: #c0392b;\n            transform: scale(1.1);\n        }\n        .info {\n            font-size: 18px;\n        }\n        .info p {\n            margin: 10px 0;\n        }\n    </style>\n</head>\n<body>\n    <div class=\"slot-machine\">\n        <div class=\"reels\">\n            <div class=\"reel\" id=\"reel1\">BAR</div>\n            <div class=\"reel\" id=\"reel2\">BAR</div>\n            <div class=\"reel\" id=\"reel3\">7</div>\n        </div>\n        <div class=\"controls\">\n            <button onclick=\"spin()\">SPIN</button>\n        </div>\n        <div class=\"info\">\n            <p>Ganancia: $<span id=\"win\">0</span></p>\n            <p>Crédito: $<span id=\"credit\">64</span></p>\n            <p>Apuesta: $<span id=\"bet\">1</span></p>\n        </div>\n    </div>\n\n    <script>\n        const symbols = [\"BAR\", \"7\", \"CHERRY\"];\n        let credit = 64;\n        let bet = 1;\n        let win = 0;\n\n        function spin() {\n            if (credit < bet) {\n                alert(\"¡No tienes suficiente crédito!\");\n                return;\n            }\n\n            credit -= bet;\n\n            const reel1 = symbols[Math.floor(Math.random() * symbols.length)];\n            const reel2 = symbols[Math.floor(Math.random() * symbols.length)];\n            const reel3 = symbols[Math.floor(Math.random() * symbols.length)];\n\n            document.getElementById(\"reel1\").innerText = reel1;\n            document.getElementById(\"reel2\").innerText = reel2;\n            document.getElementById(\"reel3\").innerText = reel3;\n\n            if (reel1 === reel2 && reel2 === reel3) {\n                win = bet * 10;\n            } else {\n                win = 0;\n            }\n\n            credit += win;\n\n            document.getElementById(\"win\").innerText = win;\n            document.getElementById(\"credit\").innerText = credit;\n        }\n    </script>\n</body>\n</htm